mapper.select:
select bookmarkId
from bookmark
where userId = #{userId} and path = #{path} and name = #{name};

select max(sort)
from bookmark
where userId = #{userId} and path = #{path}

select bookmarkId
from bookmark
where
userId = #{userId}
and (path =#{path} or path like concat(#{path},".%") );

mapper.update:
update bookmark
set name = #{name}, url = #{url},searchKey = #{searchKey}
where bookmarkId = #{bookmarkId} and userId = #{userId}

update bookmark
set sort = sort + 1
where userId = #{userId} and path = #{path} and sort >= #{sort}

update bookmark
set path = #{path}, sort = #{sort}
where userId = #{userId} and bookmarkId = #{bookmarkId}
